function() {
  try {
    var count = {{CJS - Word Count}}
    if (count == "n/a") return "n/a"

    var minutes = (count / 300)
    var sign = minutes < 0 ? "-" : "";
    var hours = Math.floor(Math.abs(minutes / 3600)) % 24;
    var min = Math.floor(Math.abs(minutes));
    var sec = Math.floor((Math.abs(minutes) * 60) % 60);
    return sign + (hours < 10 ? "0" : "") + hours + ":" + (min < 10 ? "0" : "") + min + ":" + (sec < 10 ? "0" : "") + sec;

  } catch (e) {}

  return "n/a"
}

