function(){
  var checkValue = document.title;
  // get the text to match to (in any method)
  var pageNotFoundText = {{Const - Page Not Found - Text [EDIT ME]}};
  // get the configured css selector to taget the html element that will hold display text to the user
  var pageNotFoundCSS = {{Const - Page Not Found - CSS Selector [EDIT ME]}};
  // create a regex
  var re = new RegExp(pageNotFoundText, 'ig');
  // test for the document title first
  if(re.test(checkValue)){
    return 'yes';
  } else {
    // use the css selector
    var el = document.querySelector(pageNotFoundCSS);
    if(el) {
      // get the text content from a succesful return of a targeted element
      checkValue = el.innerText || el.textContent;
      // test agaist the css selector
      if(re.test(checkValue)){
        return 'yes';
      }
    }
  }
  return 'no';
}

function(){
  // get current form
  var form = {{Form Element}};
  var name = 'no-value';
  if(form) {
    // look to see if the form has a name attribute, then an id, otherwise it will attempt to use the parent element classes
    if(typeof form.name != 'undefined' && form.name.length > 0) {
      name = form.name;

    } else if(typeof form.id != 'undefined' && form.id.length > 0) {
      name = form.id;
    } else {
      var pt = form.parentNode;
      name = pt && pt.className ? pt.className.trim() : name;
    }
  }
  return name;
}

function(){
  // get css selector from configuration
  var css = {{Const - Form - CSS Selector [EDIT ME]}};
  if(css) {
    // id a css selector has been provided apply a query select on the document and return the first match
    var form = document.querySelector(css);
    return form ? form : 'no-form';
  }
  return 'no-form';
}

function(){
  	// get the current elements clicked text
    var text = {{Click Text}};
	// remove white space
    text = text ? text.trim() : undefined;
    try {
      	// a function to cycle through attributes on the target supplied
        var checkAttributes = function(text, target, attributes){
            try {
                if(target && typeof target.getAttribute != 'undefined') {
                    for (var i = 0; i<attributes.length; i++) {
                        var value = target.getAttribute(attributes[i]);
                        if(value && value.length > 0) {
                            text = value.trim();
                            break;
                        }
                    }
                }
            } catch (err){};
            return text;
        }
        // get the current clicked element
        var currentElement = {{Click Element}};
		// check if the element has click text
        if(!text || text.length == 0){
          	// search the current element
            text = checkAttributes(text, currentElement, ['title', 'alt', 'src', 'aria-label', 'id']);
          	// does the element have children elements
          	var childs;
          	if(currentElement && typeof currentElement.children != 'undefined') {
              childs = currentElement.children;
            }
            if(childs && childs.length > 0){
              	// search the first child for preferred attrbiutes e.g an image name or title
           		text = checkAttributes(text, childs[0], ['title', 'alt', 'src', 'aria-label']);
       		}
        }
    } catch (err) {};
	// if no value is found set a defult
	if(!text || text.length == 0) {
        text = 'no-text';
    }
    return text;
}

